// ExpenseTracker.jsx (No Tailwind, using react-icons instead of lucide-react)
import React, { useState, useEffect } from 'react';
import { FaCheck, FaBell, FaUser, FaUsers, FaPlus, FaDollarSign, FaExclamationCircle, FaBalanceScale } from 'react-icons/fa';

const ExpenseTracker = () => {
  const [friends, setFriends] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [settlements, setSettlements] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [activeTab, setActiveTab] = useState('expenses');

  const [showAddFriend, setShowAddFriend] = useState(false);
  const [newFriendName, setNewFriendName] = useState('');

  const [showAddExpense, setShowAddExpense] = useState(false);
  const [newExpense, setNewExpense] = useState({
    description: '',
    amount: '',
    paidBy: '',
    splitWith: []
  });

  useEffect(() => {
    if (friends.length === 0) {
      setFriends([{ id: 1, name: 'You', isCurrentUser: true }]);
    }
  }, []);

  useEffect(() => {
    calculateSettlements();
  }, [expenses, friends]);

  const addFriend = () => {
    if (newFriendName.trim()) {
      const newFriend = {
        id: Date.now(),
        name: newFriendName.trim(),
        isCurrentUser: false
      };
      setFriends([...friends, newFriend]);
      setNewFriendName('');
      setShowAddFriend(false);
    }
  };

  const addExpense = () => {
    if (newExpense.description && newExpense.amount && newExpense.paidBy && newExpense.splitWith.length > 0) {
      const expense = {
        id: Date.now(),
        description: newExpense.description,
        amount: parseFloat(newExpense.amount),
        paidBy: parseInt(newExpense.paidBy),
        splitWith: newExpense.splitWith.map(id => parseInt(id)),
        date: new Date().toISOString(),
        settled: false
      };
      setExpenses([...expenses, expense]);
      setNewExpense({ description: '', amount: '', paidBy: '', splitWith: [] });
      setShowAddExpense(false);
      addNotification(`New expense added: ${expense.description} - $${expense.amount}`);
    }
  };

  const calculateSettlements = () => {
    const balances = {};
    friends.forEach(friend => { balances[friend.id] = 0; });
    expenses.forEach(expense => {
      const share = expense.amount / expense.splitWith.length;
      balances[expense.paidBy] += expense.amount;
      expense.splitWith.forEach(id => { balances[id] -= share; });
    });

    const newSettlements = [], debtors = [], creditors = [];
    Object.entries(balances).forEach(([id, balance]) => {
      const person = friends.find(f => f.id === parseInt(id));
      if (!person) return;
      if (balance > 0.01) creditors.push({ person, amount: balance });
      else if (balance < -0.01) debtors.push({ person, amount: -balance });
    });

    debtors.forEach(debtor => {
      creditors.forEach(creditor => {
        if (debtor.amount > 0 && creditor.amount > 0) {
          const settleAmount = Math.min(debtor.amount, creditor.amount);
          newSettlements.push({
            id: `${debtor.person.id}-${creditor.person.id}-${Date.now()}`,
            from: debtor.person,
            to: creditor.person,
            amount: settleAmount,
            status: 'pending',
            dueDate: new Date(Date.now() + 24 * 60 * 60 * 1000)
          });
          debtor.amount -= settleAmount;
          creditor.amount -= settleAmount;
        }
      });
    });

    setSettlements(newSettlements);
  };

  const addNotification = (message, type = 'info') => {
    const notification = {
      id: Date.now(),
      message,
      type,
      timestamp: new Date(),
      read: false
    };
    setNotifications(prev => [notification, ...prev]);
  };

  const markSettlementPaid = (id) => {
    setSettlements(prev => prev.map(s => s.id === id ? { ...s, status: 'completed' } : s));
    addNotification('Payment confirmed!', 'success');
  };

  const sendReminder = (settlement) => {
    addNotification(`Reminder sent to ${settlement.from.name} for $${settlement.amount.toFixed(2)}`, 'warning');
  };

  const toggleSplitWith = (id) => {
    setNewExpense(prev => ({
      ...prev,
      splitWith: prev.splitWith.includes(id)
        ? prev.splitWith.filter(x => x !== id)
        : [...prev.splitWith, id]
    }));
  };

  const markNotificationRead = (id) => {
    setNotifications(prev => prev.map(n => n.id === id ? { ...n, read: true } : n));
  };

  const unreadCount = notifications.filter(n => !n.read).length;

  return (
    <div className="container">
      <h1><FaDollarSign /> Group Expense Tracker</h1>
      <div style={{ margin: '1rem 0' }}>
        <button className="btn" onClick={() => setShowAddFriend(true)}><FaUsers size={16} /> Add Friend</button>
        <button className="btn" onClick={() => setShowAddExpense(true)}><FaPlus size={16} /> Add Expense</button>
      </div>

      <div style={{ marginBottom: '1rem' }}>
        <button className="btn" onClick={() => setActiveTab('expenses')}>Expenses</button>
        <button className="btn" onClick={() => setActiveTab('settlements')}>Settlements</button>
        <button className="btn" onClick={() => setActiveTab('notifications')}>
          Notifications {unreadCount > 0 && `(${unreadCount})`}
        </button>
      </div>

      <div>
        {activeTab === 'expenses' && (
          <div>
            <h2>Expenses</h2>
            {expenses.length === 0 ? <p>No expenses yet.</p> : expenses.map(e => (
              <div key={e.id} className="card">
                <strong>{e.description}</strong> - ${e.amount.toFixed(2)}<br />
                Paid by: {friends.find(f => f.id === e.paidBy)?.name}<br />
                Split with: {e.splitWith.map(id => friends.find(f => f.id === id)?.name).join(', ')}
              </div>
            ))}
          </div>
        )}

        {activeTab === 'settlements' && (
          <div>
            <h2>Settlements</h2>
            {settlements.length === 0 ? <p>All settled up!</p> : settlements.map(s => (
              <div key={s.id} className="card">
                <FaUser /> {s.from.name} owes <FaUser /> {s.to.name} ${s.amount.toFixed(2)} ({s.status})
                {s.status === 'pending' && (
                  <div>
                    <button className="btn" onClick={() => markSettlementPaid(s.id)}><FaCheck /> Mark Paid</button>
                    <button className="btn" onClick={() => sendReminder(s)}><FaBell /> Remind</button>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {activeTab === 'notifications' && (
          <div>
            <h2>Notifications</h2>
            {notifications.length === 0 ? <p>No notifications.</p> : notifications.map(n => (
              <div key={n.id} className="card">
                {n.type === 'success' && <FaCheck />} 
                {n.type === 'warning' && <FaExclamationCircle />} 
                {n.type === 'info' && <FaBell />} 
                {n.message} ({n.timestamp.toLocaleString()})
                {!n.read && <button className="btn" onClick={() => markNotificationRead(n.id)}>Mark Read</button>}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Friend Modal */}
      {showAddFriend && (
        <div className="card">
          <h3>Add Friend</h3>
          <input value={newFriendName} onChange={(e) => setNewFriendName(e.target.value)} />
          <button className="btn" onClick={addFriend}>Add</button>
          <button className="btn" onClick={() => setShowAddFriend(false)}>Cancel</button>
        </div>
      )}

      {/* Expense Modal */}
      {showAddExpense && (
        <div className="card">
          <h3>Add Expense</h3>
          <input placeholder="Description" value={newExpense.description} onChange={(e) => setNewExpense(prev => ({ ...prev, description: e.target.value }))} /><br />
          <input type="number" placeholder="Amount" value={newExpense.amount} onChange={(e) => setNewExpense(prev => ({ ...prev, amount: e.target.value }))} /><br />
          <select value={newExpense.paidBy} onChange={(e) => setNewExpense(prev => ({ ...prev, paidBy: e.target.value }))}>
            <option value="">Who paid?</option>
            {friends.map(f => <option key={f.id} value={f.id}>{f.name}</option>)}
          </select><br />
          <label>Split with:</label><br />
          {friends.map(f => (
            <label key={f.id}>
              <input type="checkbox" checked={newExpense.splitWith.includes(f.id)} onChange={() => toggleSplitWith(f.id)} /> {f.name}
            </label>
          ))}<br />
          <button className="btn" onClick={addExpense}>Add Expense</button>
          <button className="btn" onClick={() => setShowAddExpense(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default ExpenseTracker;
